clear allwd = cd;cd(['/Users/daniel/NMRdata/AVII500/DT/'])% ExpNam = {'CPMAS4mm_setup'};% expno = 35:36;% basl = [.02 .16 .2 .27 .47 .65 .69 .76 .79 .82 .85 .98];% lb = 20;% si = 2*1024;% ppmshift = 0;% lb1 = 10;% si1 = 512;% ppmshift1 = 0;ExpNam = {'C10E4relax'};expno = 71:90;basl = [.02 .1 .14 .2 .37 .68 .72 .79 .86 .98];lb = 20;%si = 32*1024;ppmshift = 0;lb1 = 5;si1 = 64;ppmshift1 = 0;CheckBasline = 0;AutoPhase = 0; phc0 = 0; phc1 = 0;Imin = .1;Imax = 1;cd(ExpNam{1})for nexp = 1:length(expno)    Spec2Dtd1         %figure(1), clf, surf(abs(Itd1)), shading('flat'), view(0,90), return    dw1 = NMRacqus.inp1*1e-6;    t1 = NMRacqus.p1*1e-6 + dw1*(0:(td1-1));    swh1 = 1/dw1;    nu1 = swh1*linspace(0, 1, si1); nu1 = nu1 - nu1(si1/2+1);    [dummy,t1array] = ndgrid(1:si,t1);    lbfun1 = .5*(cos(-1/max(t1)*pi*t1array)+1);    I = fftshift(fft(Itd1.*lbfun1,si1,2),2);    %figure(1), clf, surf(abs(I)), shading('flat'), view(0,90), return    I = abs(sum(I,1));        posmax = find(I == max(I));    posmax = posmax(1);    B1 = abs(nu1(posmax));    figure(1), clf, plot(nu1,I,B1,I(posmax),'o')    pause(1)        NutDat.B1 = B1;    NutDat.pl = NMRacqus.pl1;        eval(['save ' num2str(expno(nexp)) '/NutDat NutDat'])end