                     Pout = Pnorm.*lsqcurvefit(Funam,Pin./Pnorm,Xin/Xnorm,Yin./Ynorm,LB./Pnorm,UB./Pnorm,options,Pnorm,Xnorm,Ynorm);                            Xthresh = -log(ThreshFit)/Pout(2);                            Weight = .5-.5*erf(5*(Xin-Xthresh)/Xthresh);                            Pout = Pin; Ynorm = mean(Yin)./(Weight+1e-10 ); Xnorm = mean(Xin); Pnorm = Pin;                             Pout = Pnorm.*lsqcurvefit(Funam,Pin./Pnorm,Xin/Xnorm,Yin./Ynorm,LB./Pnorm,UB./Pnorm,options,Pnorm,Xnorm,Ynorm);                            Yout = feval(Funam,Pout,Xin); error = Yin - Yout;                            Ycalc_aniso = Yout(1:(length(Yout)/2));                            Ycalc_iso = Yout((length(Yout)/2+1):length(Yout));                            mu2_aniso = Pout(3)^2;                            mu2_iso = Pout(4)^2;                             mD_gamma = Pout(2);                            S0_gamma = Pout(1);                                                        Ycalc_mD = S0_gamma*exp(-mD_gamma*Xin_iso);                                                                subplot(2,2,4)                            semilogy(Xin_aniso(fitpoints),Ycalc_aniso(fitpoints),'r-')                            hold on                            semilogy(Xin_aniso,Yin_aniso,'ro')                            semilogy(Xin_iso,Yin_iso,'bs')                            semilogy(Xin_iso(fitpoints),Ycalc_iso(fitpoints),'b-')                            semilogy(Xin_iso(fitpoints),Ycalc_mD(fitpoints),'k-')                            semilogy(Xin,S0_gamma*Weight,'k--')                            hold off                            set(gca,'XLim',Xthresh*[-.1 1.1],'YLim',S0*[ThreshFit 1.1])                                                        uFA = sqrt(3/2)*sqrt(1/(1+2/5*mD_gamma^2/(mu2_aniso-mu2_iso)));                            if mu2_iso>mu2_aniso                                uFA = 0;                            end                                                        K_iso = mu2_iso/mD_gamma^2;                            title(['K=' num2str(K_iso,2) ' uFA=' num2str(uFA,2)])                                                        subplot(2,2,1)                            Iplot = abs(squeeze(Itd1(:,:,(NMRacqu2s.td/Ndir+1))));                            colormap('gray')                            Iplot = Iplot/max(max(Iplot));                            Iplot(Iplot>thresh) = 1;                            clim = [0 1];                            imagesc(r.i*1e3,r.j*1e3,Iplot',clim)                            set(gca,'YDir','normal')                            axis equal, axis tight                            hold on                            plot(r.i(ni)*1e3,r.j(nj)*1e3,'ro')                            title(['ni=' num2str(ni) ' nj=' num2str(nj)])                                                                                    Images.K(ni,nj) = K_iso;                            Images.uFA(ni,nj) = uFA;                            Images.mu2_iso(ni,nj) = mu2_iso;                            Images.mu2_aniso(ni,nj) = mu2_aniso;                            Images.S0_gamma(ni,nj) = S0_gamma;                            Images.mD_gamma(ni,nj) = mD_gamma;                            pause(.1)                                                   end                                      end                end                Images.ADC = 1/3*(Images.lambda1+Images.lambda2+Images.lambda3);                Images.FA = sqrt(1/2)*sqrt((Images.lambda1-Images.lambda2).^2+(Images.lambda1-Images.lambda3).^2+(Images.lambda2-Images.lambda3).^2)...                    ./sqrt(Images.lambda1.^2+Images.lambda2.^2+Images.lambda3.^2);                Images.FA(find(isnan(Images.FA)==1)) = 0;                Images.CL = (Images.lambda1 - Images.lambda2)./Images.lambda1;                Images.CL(find(isnan(Images.CL)==1)) = 0;                Images.CP = (Images.lambda2 - Images.lambda3)./Images.lambda1;                Images.CP(find(isnan(Images.CP)==1)) = 0;                eval(['save ' num2str(expno(nexp)) '/Images Images r'])            else                eval(['load ' num2str(expno(nexp)) '/Images'])            end            %%            %Images.Dxyz.r = sqrt(Images.Dxx.^2 + Images.Dyy.^2 + Images.Dzz.^2);            Images.Dxyz.r = Images.lambda1;            Images.Dxyz.x = Images.Dxx./Images.Dxyz.r;            Images.Dxyz.y = Images.Dyy./Images.Dxyz.r;            Images.Dxyz.z = Images.Dzz./Images.Dxyz.r;                        ADCmax = max(reshape(Images.ADC,numel(Images.ADC),1));            Dmax = max([max(reshape(Images.Dxx,numel(Images.ADC),1)) max(reshape(Images.Dyy,numel(Images.ADC),1)) max(reshape(Images.Dzz,numel(Images.ADC),1))]);            ADCmax = 2e-9;            Dmax = 2e-9;                        width = .2;            figure(3), clf            colormap('gray')            axes('position',[.1 .6 width .35])            Iplot = squeeze(Images.S0(:,:)/max(max(Images.S0)));            clim = [0 1];            imagesc(r.i*1e3,r.j*1e3,Iplot',clim)            set(gca,'YDir','normal')            axis equal, axis tight, axis off            title('S0')            %colorbar            axes('position',[.3 .6 width .35])            Iplot = squeeze(Images.ADC(:,:));            clim = [0 ADCmax];            imagesc(r.i*1e3,r.j*1e3,Iplot',clim)            set(gca,'YDir','normal')            axis equal, axis tight, axis off            title('ADC')            %colorbar            axes('position',[.5 .6 width .35])            [nudim.j,nudim.i] = size(Images.S0');            Iplot = zeros(nudim.j,nudim.i,3);%             Iplot(:,:,1) = squeeze(Images.Dxx(:,:))'/Dmax;%             Iplot(:,:,2) = squeeze(Images.Dyy(:,:))'/Dmax;%             Iplot(:,:,3) = squeeze(Images.Dzz(:,:))'/Dmax;            Iplot(:,:,1) = (Images.FA.*Images.Dxyz.x)';            Iplot(:,:,2) = (Images.FA.*Images.Dxyz.y)';            Iplot(:,:,3) = (Images.FA.*Images.Dxyz.z)';%             Iplot(:,:,1) = (Images.FA.*(1-Images.Dxyz.x))';%             Iplot(:,:,2) = (Images.FA.*(1-Images.Dxyz.y))';%             Iplot(:,:,3) = (Images.FA.*(1-Images.Dxyz.z))';            Iplot(find(Iplot>1)) = 1;            Iplot(find(Iplot<0)) = 0;            imagesc(r.i*1e3,r.j*1e3,Iplot)            set(gca,'YDir','normal')            axis equal, axis tight, axis off            title('Dxx,Dyy,Dzz')            axes('position',[.7 .6 width .35])            Iplot = squeeze(Images.K(:,:));            clim = [0 1];            imagesc(r.i*1e3,r.j*1e3,Iplot',clim)            set(gca,'YDir','normal')            axis equal, axis tight, axis off            title(['K max = ' num2str(max(max(Images.K)),2)])             axes('position',[.1 .1 width .35])            Iplot = squeeze(Images.FA(:,:));            clim = [0 1];            imagesc(r.i*1e3,r.j*1e3,Iplot',clim)            set(gca,'YDir','normal')            axis equal, axis tight            xlabel('read / mm'), ylabel('phase / mm')            title(['FA max = ' num2str(max(max(Images.FA)),2)])            %colorbar            axes('position',[.3 .1 width .35])            Iplot = squeeze(Images.uFA(:,:));            clim = [0 1];            imagesc(r.i*1e3,r.j*1e3,Iplot',clim)            set(gca,'YDir','normal')            axis equal, axis tight, axis off            title('uFA')            %colorbar            axes('position',[.5 .1 width .35])            Iplot = squeeze(Images.CP(:,:));            clim = [0 1];            imagesc(r.i*1e3,r.j*1e3,Iplot',clim)            set(gca,'YDir','normal')            axis equal, axis tight, axis off            title('CP')            %colorbar            axes('position',[.7 .1 width .35])            Iplot = squeeze(Images.CL(:,:));            clim = [0 1];            imagesc(r.i*1e3,r.j*1e3,Iplot',clim)            set(gca,'YDir','normal')            axis equal, axis tight, axis off            title('CL')            %colorbar%%            delete([num2str(expno(nexp)) '/ReportFig*'])            delete([num2str(expno(nexp)) '/acqu*sconv'])            delete([num2str(expno(nexp)) '/*Dat.mat'])            delete([num2str(expno(nexp)) '/*.jpg'])            delete([num2str(expno(nexp)) '/*.eps'])            set(gcf, 'PaperPosition', [0 0 11 8.5],'PaperSize', [11 8.5]);             eval(['print ' num2str(expno(nexp)) '/ReportFig -loose -dpdf'])        end    end    cd ..endcd(wd)