clear allwd = cd;cd(['/Users/daniel/NMRdata/AVII500/DT/'])ExpNam = {'CPMAS4mm_setup'};expno = 35:54;basl = [.02 .16 .2 .27 .47 .65 .69 .76 .79 .82 .85 .98];lb = 20;si = 4*1024;ppmshift = 0;CheckBasline = 0;AutoPhase = 1;cd(ExpNam{1})for nexp = 1:length(expno)    Spec2Dtd1        I = real(I);    Itd1 = real(Itd1);    fontsize = 20;    linewidth = 1;    figure(1), clf    axes('position',[.02 .18 .96 .3],'FontSize',fontsize*.8)    plot(ppm,I/max(I),'k','LineWidth',linewidth)    set(gca,'XDir','reverse','YTick',[],'LineWidth',1.5*linewidth,'Box','off','TickDir','out','Ycolor',[1 1 1])    axis([min(ppm) max(ppm) 1*[min([min(I/max(I)) -.05]) 1.05]])    xlabel(['\delta(' NMRacqus.nuc1 ') / ppm'],'FontSize',fontsize)    axes('position',[.02 .5 .96 .5],'FontSize',fontsize*.8)    surf(ppm,1:td1,Itd1')    view(0,90)    shading('flat')    axis tight off    zlim = get(gca,'ZLim');    zlim = zlim + .5*diff(zlim)*[-1 1];    set(gca,'XDir','reverse','ZLim',zlim)    caxis(max(max(Itd1))*[-1 1])    colormap('default')    colormap('hot')    hot = colormap;    hot = hot(20:64,:);    cool = [hot(:,3) hot(:,2) hot(:,1)];    hotcool = [cool; flipud(hot)];    colormap(hotcool)    pause(.1)     dat.I = I;    dat.ppm = ppm;    dat.Itd1 = Itd1;        %dat = [ppm I]; save SpecDat.txt dat -ascii -tabs    %save([num2str(expno(nexp)) '/SpecDat.mat'],'dat')    eval(['print ' num2str(expno(nexp)) '/ReportFig -loose -depsc'])end