clear alldata_path = '/Users/daniel/Dropbox/NMRdata/SNC/DNP';expnam = '210510_SC_Topgaard';expno = [10:18 20:23 25:48];nii_fn = fullfile(data_path,expnam,['data_' num2str(min(expno)) 'to' num2str(max(expno))],'nii_xps','data.nii.gz');nexp = 1;spec_fn = fullfile(data_path,expnam,num2str(expno(nexp)),'SpecDat.mat');load(spec_fn)I = Spec.I;ppm = Spec.ppm;ppm1 = Spec.ppm1;sz = size(I);xps.n = numel(expno);data = zeros(sz(1),sz(2),1,xps.n);xps.d1 = zeros(xps.n,1);xps.d8 = zeros(xps.n,1);xps.p15 = zeros(xps.n,1);for nexp = 1:length(expno)        spec_fn = fullfile(data_path,expnam,num2str(expno(nexp)),'SpecDat.mat');    load(spec_fn)    data(:,:,1,nexp) = Spec.I;    NMRacqus_fn = fullfile(data_path,expnam,num2str(expno(nexp)),'NMRacqus.mat');    load(NMRacqus_fn)    xps.d1(nexp) = NMRacqus.d1;    xps.d8(nexp) = NMRacqus.d8;    xps.p15(nexp) = 1e-6*NMRacqus.p15;end% make nifti headearh = mdm_nii_h_empty;sdim = size(data);h.pixdim(1+(1:length(sdim))) = sdim;% h.pixdim(2:4) = [PVM_SpatResol PVM_SliceThick];h.xyzt_units = 'SI';% figure(12), clf, im2d = squeeze(data(:,:,2,1)); imagesc(im2d), return% write nifti image and headermdm_nii_write(data, nii_fn, h, 0);save(mdm_fn_nii2xps(nii_fn),'xps');