function Y = fDiffVACSYbiexp(Pin,Xin,Xin2,Pnorm,Xnorm,Ynorm)if nargin == 3    Pnorm = ones(size(Pin));    Xnorm = 1;    Ynorm = 1;endPin = Pin.*Pnorm;Xin = Xin*Xnorm;I0 = Pin(1);Dparf = Pin(2);Dperpf = Pin(3);Dpars = Pin(4);Dperps = Pin(5);ff = Pin(6);I = I0.*(ff.*fDiffVACSY([1 Dparf Dperpf],Xin,Xin2) + (1-ff).*fDiffVACSY([1 Dpars Dperps],Xin,Xin2));Y = I/Ynorm;