function Y = fDiffVACSYbiexp2(Pin,Xin,Xin2,Pnorm,Xnorm,Ynorm)if nargin == 3    Pnorm = ones(size(Pin));    Xnorm = 1;    Ynorm = 1;endPin = Pin.*Pnorm;Xin = Xin*Xnorm;I0 = Pin(1);Disof = Pin(2);DeltaDf = Pin(3);Disos = Pin(4);DeltaDs = Pin(5);ff = Pin(6);I = I0.*(ff.*fDiffVACSY2([1 Disof DeltaDf],Xin,Xin2) + (1-ff).*fDiffVACSY2([1 Disos DeltaDs],Xin,Xin2));Y = I/Ynorm;