function Y = fDiffVACSYtricomp(Pin,Xin,Xin2,Pnorm,Xnorm,Ynorm)if nargin == 3    Pnorm = ones(size(Pin));    Xnorm = 1;    Ynorm = 1;endPin = Pin.*Pnorm;Xin = Xin*Xnorm;I0_1 = Pin(1);I0_2 = Pin(2);I0_3 = Pin(3);Diso_1 = Pin(4);Diso_2 = Pin(5);Diso_3 = Pin(6);DeltaD_1 = Pin(7);DeltaD_2 = Pin(8);DeltaD_3 = Pin(9);I =   fDiffVACSY2([I0_1 Diso_1 DeltaD_1],Xin,Xin2)...    + fDiffVACSY2([I0_2 Diso_2 DeltaD_2],Xin,Xin2)...    + fDiffVACSY2([I0_3 Diso_3 DeltaD_3],Xin,Xin2);Y = I/Ynorm;