function Y = fIVIM(Pin,Xin,Pnorm,Xnorm,Ynorm,Deltavector,deltavector);if nargin == 2    Pnorm = ones(size(Pin));    Xnorm = 1;    Ynorm = 1;endPin = Pin.*Pnorm;Xin = Xin*Xnorm;I0 = Pin(1);v_A = Pin(2);D_B = Pin(3);f_A = Pin(4);D_A = D_B;v_B = 0;qvector = Xin;tdvector = Deltavector-deltavector/3;E_A = exp(-4*pi^2*qvector.^2.*tdvector*D_A).*exp(-4*pi^2*qvector.^2.*Deltavector.^2*v_A^2);E_B = exp(-4*pi^2*qvector.^2.*tdvector*D_B).*exp(-4*pi^2*qvector.^2.*Deltavector.^2*v_B^2);E = f_A*E_A + (1-f_A)*E_B;I = I0.*E;Y = I./Ynorm;