function Y = fIVIM_vcomp2(Pin,Xin,Pnorm,Xnorm,Ynorm,Para);Pin = Pin.*Pnorm;Xin = Xin*Xnorm;I0_ncomp = Pin(1);I0_vcomp = I0_ncomp;Dt = Pin(2);Dp = Dt;vp = Pin(3);fp = Pin(4);qvector_ncomp = Xin(:,1);qvector_vcomp = Xin(:,2);delta_ncomp = Para.delta_ncomp;Delta_ncomp = Para.Delta_ncomp;delta_vcomp  = Para.delta_vcomp ;Delta_vcomp  = Para.Delta_vcomp ;Et_ncomp = exp(-4*pi^2*qvector_ncomp.^2*(Delta_ncomp-delta_ncomp/3)*Dt);Ep_ncomp = exp(-4*pi^2*qvector_ncomp.^2*(Delta_ncomp-delta_ncomp/3)*Dp).*exp(-4*pi^2*qvector_ncomp.^2*Delta_ncomp^2*vp^2);E_ncomp = (1-fp)*Et_ncomp + fp*Ep_ncomp;I_ncomp = I0_ncomp*E_ncomp;Et_vcomp = exp(-2*4*pi^2*qvector_vcomp.^2*(Delta_vcomp-delta_vcomp/3)*Dt);Ep_vcomp = exp(-2*4*pi^2*qvector_vcomp.^2*(Delta_vcomp-delta_vcomp/3)*Dp);E_vcomp = (1-fp)*Et_vcomp + fp*Ep_vcomp;I_vcomp = I0_vcomp*E_vcomp;Y = [I_ncomp I_vcomp]/Ynorm;