function Y = fMurdayCotts(Pin,Xin,Pnorm,Xnorm,Ynorm,alphaR,D,delta,DELTA);Pin = Pin.*Pnorm;G2 = Xin'*Xnorm;Y0 = Pin(1);R = Pin(2);gamma = 26.75e7;alpha = alphaR/R;[alphaarray,G2array] = ndgrid(alpha,G2);Y = Y0*exp(-2*gamma^2*G2/D.*sum(alphaarray.^-4./(alphaarray.^2*R^2-2).*(2*delta-(2+exp(-alphaarray.^2*D*(DELTA-delta))-2*exp(-alphaarray.^2*D*delta)-2*exp(-alphaarray.^2*D*DELTA)+exp(-alphaarray.^2*D*(DELTA+delta)))./alphaarray.^2/D)));Y = Y'/Ynorm;