function Y = fSumLorentz2(Pin,Xin,Pnorm,Xnorm,Ynorm);Pin = Pin.*Pnorm;nu = Xin*Xnorm;npeak = (length(Pin)-1)/3;T2 = Pin(1:npeak);ampl = Pin(npeak+1:2*npeak);offset = Pin(2*npeak+1:3*npeak);phase = Pin(3*npeak+1);Icalc = zeros(size(nu));for npeak = 1:length(ampl)	Icalc = Icalc + real(fLorentz(nu,ampl(npeak),T2(npeak),offset(npeak),phase));endIcalc = real(Icalc);Y = Icalc/Ynorm;