function Y = fSumSuperLorentz(Pin,Xin,Pnorm,Xnorm,Ynorm);Pin = Pin.*Pnorm;nu = Xin*Xnorm;npeak = length(Pin)/4;T2min = Pin(1:npeak);T2max = Pin(npeak+1:2*npeak);ampl = Pin(2*npeak+1:3*npeak);offset = Pin(3*npeak+1:4*npeak);phase = 0;Icalc = zeros(size(nu));for npeak = 1:length(ampl)    Icalc = Icalc + fSuperLorentz(nu,ampl(npeak),T2min(npeak),T2max(npeak),offset(npeak),phase);	%Icalc = Icalc + fLorentz(nu,ampl(npeak),T2(npeak),offset(npeak),phase);endIcalc = real(Icalc);Y = Icalc/Ynorm;Y(find(isnan(Y)==1)) = 0;Y(find(isinf(Y)==1)) = 0;