function Y = fbigamma(Pin,Xin,Pnorm,Xnorm,Ynorm);if nargin == 2    Pnorm = ones(size(Pin));    Xnorm = 1;    Ynorm = 1;endPin = Pin.*Pnorm;Xin = Xin*Xnorm;I0 = Pin(1);Df = Pin(2);Ds = Pin(3);sigmaf = Pin(4);sigmas = Pin(5);ff = Pin(6);If = (1 + Xin.*(sigmaf.^2)./Df).^(-1*(Df./sigmaf).^2);Is = (1 + Xin.*(sigmas.^2)./Ds).^(-1*(Ds./sigmas).^2);I = I0.*(ff.*If + (1-ff).*Is);Y = I/Ynorm;