function Y = fexpDTI(Pin,Xin,Pnorm,Xnorm,Ynorm,Gnorm);Pin = Pin.*Pnorm;Xin = Xin*Xnorm;Y0 = Pin(1);Dmean = Pin(2);fA = Pin(3);director.theta = Pin(4);director.phi = Pin(5);Dpar = Dmean.*(1+fA);Dperp = Dmean.*(1-fA/2);director.z = cos(director.theta);director.y = sin(director.theta).*sin(director.phi);director.x = sin(director.theta).*cos(director.phi);theta = acos((director.x.*Gnorm.x + director.y.*Gnorm.y + director.z.*Gnorm.z));D = Dpar.*cos(theta).^2 + Dperp.*sin(theta).^2;Y = Y0.*exp( - D.*Xin);Y = Y/Ynorm;