function Y = fisoaniso2comp(Pin,Xin1,Xin2,Pnorm,Xnorm,Ynorm)if nargin == 3    Pnorm = ones(size(Pin));    Xnorm = 1;    Ynorm = 1;endPin = Pin.*Pnorm;Xin1 = Xin1*Xnorm;Xin2 = Xin2*Xnorm;I0 = Pin(1);Dpara = Pin(2);Dperpa = Pin(3);Dparb = Pin(4);Dperpb = Pin(5);fb = Pin(6);Pin = [1 Dpara Dperpa];Ya = fisoaniso3D(Pin,Xin1,Xin2);Pin = [1 Dparb Dperpb];Yb = fisoaniso3D(Pin,Xin1,Xin2);Y = I0*((1-fb)*Ya + fb*Yb);Y = Y/Ynorm;