function Y = fmuaniso(Pin,Xin,Pnorm,Xnorm,Ynorm);if nargin == 2    Pnorm = ones(size(Pin));    Xnorm = 1;    Ynorm = 1;endPin = Pin.*Pnorm;Xin = Xin*Xnorm;Y0 = Pin(1);D = Pin(2);sigma_aniso = Pin(3);sigma_iso = Pin(4);npoints = length(Xin)/2;Xin = Xin(1:npoints);Y_aniso = Y0.*((1 + Xin.*(sigma_aniso.^2)./D).^(-1*(D./sigma_aniso).^2));Y_iso = Y0.*((1 + Xin.*(sigma_iso.^2)./D).^(-1*(D./sigma_iso).^2));Y = [Y_aniso; Y_iso]./Ynorm;Y = real(Y);