function Y = fsincvoigtfid(Pin,Xin,Pnorm,Xnorm,Ynorm);if nargin == 2    Pnorm = ones(size(Pin));    Xnorm = 1;    Ynorm = 1;endPin = Pin.*Pnorm;Xin = Xin*Xnorm;S0 = Pin(1);ff = Pin(2);a = Pin(3);b = Pin(4);wL = Pin(5);wG = Pin(6);Sf = exp(-a^2*Xin.^2/2).*sin(b*Xin)./(b*Xin);Ss = exp(-pi*wL.*Xin - (pi*wG*Xin/2/sqrt(log(2))).^2);S = S0.*(ff.*Sf + (1-ff).*Ss);Y = S/Ynorm;