function gro_out = fRotateGro(gro_in,eulerangles)% gro_out = fRotateGro(gro_in,eulerangles)%% Active right-handed rotation% First gamma around z, then beta around y, and finally alpha around zgro_out = gro_in;alpha = eulerangles.alpha;beta  = eulerangles.beta;gamma = eulerangles.gamma;rotmat_alpha = [    cos(alpha) -sin(alpha) 0    sin(alpha) cos(alpha) 0    0           0          1];rotmat_beta = [    cos(beta)  0 sin(beta)    0          1 0    -sin(beta) 0 cos(beta)];rotmat_gamma = [    cos(gamma)  -sin(gamma) 0    sin(gamma)  cos(gamma) 0    0           0          1];rotmat = rotmat_alpha*rotmat_beta*rotmat_gamma;%rotmatinv = inv(rotmat);RotMat.xx = rotmat(1,1);RotMat.xy = rotmat(1,2);RotMat.xz = rotmat(1,3);RotMat.yx = rotmat(2,1);RotMat.yy = rotmat(2,2);RotMat.yz = rotmat(2,3);RotMat.zx = rotmat(3,1);RotMat.zy = rotmat(3,2);RotMat.zz = rotmat(3,3);gro_out.x = RotMat.xx.*gro_in.x + RotMat.xy.*gro_in.y + RotMat.xz.*gro_in.z;gro_out.y = RotMat.yx.*gro_in.x + RotMat.yy.*gro_in.y + RotMat.yz.*gro_in.z;gro_out.z = RotMat.zx.*gro_in.x + RotMat.zy.*gro_in.y + RotMat.zz.*gro_in.z;