function acf = fTraj2RotACF(uvec,N)sz = size(uvec.x);acf = zeros(N,sz(2));for n = 1:N    Nsum = sz(1)-n+1;        ind1 = 1:Nsum;    ind2 = n:sz(1);          costheta = uvec.x(ind1,:).*uvec.x(ind2,:) + ...        uvec.y(ind1,:).*uvec.y(ind2,:) + ...        uvec.z(ind1,:).*uvec.z(ind2,:);        acf(n,:) = 1/Nsum*sum(.5*(3*costheta.^2-1),1);       endacf = mean(acf,2);