function saupe = fTraj2Saupe(uvec)saupe.xx = (3*uvec.x.*uvec.x - 1)/2;saupe.yy = (3*uvec.y.*uvec.y - 1)/2;saupe.zz = (3*uvec.z.*uvec.z - 1)/2;saupe.xy = (3*uvec.x.*uvec.y - 0)/2;saupe.xz = (3*uvec.x.*uvec.z - 0)/2;saupe.yz = (3*uvec.y.*uvec.z - 0)/2;saupe.xx = mean(saupe.xx(:));saupe.yy = mean(saupe.yy(:));saupe.zz = mean(saupe.zz(:));saupe.xy = mean(saupe.xy(:));saupe.xz = mean(saupe.xz(:));saupe.yz = mean(saupe.yz(:));