clear allexpno = 11;for nexp = 1:length(expno)    res = fExpnoInfo('Y','N','N',expno(nexp));    eval(['cd ' num2str(expno(nexp))])    load NMRacqus    load NMRacqu2s    fid = fopen('vdlist');    vd = fscanf(fid,'%f');    fclose(fid);    fid = fopen('ser','r','ieee-le');    S = fread(fid,[2, NMRacqus.td*NMRacqu2s.td/2],'long')';    S = S(:,1) + i*S(:,2);    figure(1), clf, plot(real(S)), pause        S = reshape(S,NMRacqus.td/2,NMRacqu2s.td);end