function [Rxx,Rxy,Rxz,Ryx,Ryy,Ryz,Rzx,Rzy,Rzz] = fCompRF(nu0,nu1,nuRF,tp,phi,sc)for n = 1:length(phi)	omega = (nu0 - nuRF(n))*2*pi;	omega1 = sc(n).*nu1*2*pi;	Omega = sqrt(omega1.^2 + omega.^2);	if n==1		Lxx0 = 1/2*(cos(Omega.*tp(n)).*Omega.^2+omega1.^2+cos(Omega.*tp(n)).*omega.^2)./(Omega.^2)+1/2*omega1.^2.*(1-cos(Omega.*tp(n))).*cos(2*phi(n))./(Omega.^2);		Lyy0 = 1/2*(cos(Omega.*tp(n)).*Omega.^2+omega1.^2+cos(Omega.*tp(n)).*omega.^2)./(Omega.^2)-1/2.*omega1.^2.*(1-cos(Omega.*tp(n))).*cos(2*phi(n))./(Omega.^2);		Lzz0 = omega.^2./(Omega.^2)+omega1.^2.*cos(Omega.*tp(n))./(Omega.^2);		Lxy0 = 1/2*omega1.^2.*(1-cos(Omega.*tp(n))).*sin(2*phi(n))./(Omega.^2)-omega.*sin(Omega.*tp(n))./Omega;		Lyx0 = 1/2*omega1.^2.*(1-cos(Omega.*tp(n))).*sin(2*phi(n))./(Omega.^2)+omega.*sin(Omega.*tp(n))./Omega;		Lxz0 = omega1.*omega.*(1-cos(Omega.*tp(n))).*cos(phi(n))./(Omega.^2)+omega1.*sin(Omega.*tp(n)).*sin(phi(n))./Omega;		Lzx0 = omega1.*omega.*(1-cos(Omega.*tp(n))).*cos(phi(n))./(Omega.^2)-omega1.*sin(Omega.*tp(n)).*sin(phi(n))./Omega;		Lyz0 = omega1.*omega.*(1-cos(Omega.*tp(n))).*sin(phi(n))./(Omega.^2)-omega1.*sin(Omega.*tp(n)).*cos(phi(n))./Omega;		Lzy0 = omega1.*omega.*(1-cos(Omega.*tp(n))).*sin(phi(n))./(Omega.^2)+omega1.*sin(Omega.*tp(n)).*cos(phi(n))./Omega;	else		Lxx1 = 1/2*(cos(Omega.*tp(n)).*Omega.^2+omega1.^2+cos(Omega.*tp(n)).*omega.^2)./(Omega.^2)+1/2*omega1.^2.*(1-cos(Omega.*tp(n))).*cos(2*phi(n))./(Omega.^2);		Lyy1 = 1/2*(cos(Omega.*tp(n)).*Omega.^2+omega1.^2+cos(Omega.*tp(n)).*omega.^2)./(Omega.^2)-1/2.*omega1.^2.*(1-cos(Omega.*tp(n))).*cos(2*phi(n))./(Omega.^2);		Lzz1 = omega.^2./(Omega.^2)+omega1.^2.*cos(Omega.*tp(n))./(Omega.^2);		Lxy1 = 1/2*omega1.^2.*(1-cos(Omega.*tp(n))).*sin(2*phi(n))./(Omega.^2)-omega.*sin(Omega.*tp(n))./Omega;		Lyx1 = 1/2*omega1.^2.*(1-cos(Omega.*tp(n))).*sin(2*phi(n))./(Omega.^2)+omega.*sin(Omega.*tp(n))./Omega;		Lxz1 = omega1.*omega.*(1-cos(Omega.*tp(n))).*cos(phi(n))./(Omega.^2)+omega1.*sin(Omega.*tp(n)).*sin(phi(n))./Omega;		Lzx1 = omega1.*omega.*(1-cos(Omega.*tp(n))).*cos(phi(n))./(Omega.^2)-omega1.*sin(Omega.*tp(n)).*sin(phi(n))./Omega;		Lyz1 = omega1.*omega.*(1-cos(Omega.*tp(n))).*sin(phi(n))./(Omega.^2)-omega1.*sin(Omega.*tp(n)).*cos(phi(n))./Omega;		Lzy1 = omega1.*omega.*(1-cos(Omega.*tp(n))).*sin(phi(n))./(Omega.^2)+omega1.*sin(Omega.*tp(n)).*cos(phi(n))./Omega;		Lxx2 = Lxx1.*Lxx0 + Lxy1.*Lyx0 + Lxz1.*Lzx0;		Lxy2 = Lxx1.*Lxy0 + Lxy1.*Lyy0 + Lxz1.*Lzy0;		Lxz2 = Lxx1.*Lxz0 + Lxy1.*Lyz0 + Lxz1.*Lzz0;		Lyx2 = Lyx1.*Lxx0 + Lyy1.*Lyx0 + Lyz1.*Lzx0;		Lyy2 = Lyx1.*Lxy0 + Lyy1.*Lyy0 + Lyz1.*Lzy0;		Lyz2 = Lyx1.*Lxz0 + Lyy1.*Lyz0 + Lyz1.*Lzz0;		Lzx2 = Lzx1.*Lxx0 + Lzy1.*Lyx0 + Lzz1.*Lzx0;		Lzy2 = Lzx1.*Lxy0 + Lzy1.*Lyy0 + Lzz1.*Lzy0;		Lzz2 = Lzx1.*Lxz0 + Lzy1.*Lyz0 + Lzz1.*Lzz0;				Lxx0 = Lxx2;		Lyy0 = Lyy2;		Lzz0 = Lzz2;		Lxy0 = Lxy2;		Lyx0 = Lyx2;		Lxz0 = Lxz2;		Lzx0 = Lzx2;		Lyz0 = Lyz2;		Lzy0 = Lzy2;	endendRxx = Lxx0;Ryy = Lyy0;Rzz = Lzz0;Rxy = Lxy0;Ryx = Lyx0;Rxz = Lxz0;Rzx = Lzx0;Ryz = Lyz0;Rzy = Lzy0;