function [Rxx,Rxy,Rxz,Ryx,Ryy,Ryz,Rzx,Rzy,Rzz] = fRF(nu0,nu1,nuRF,t,phi)omega = (nu0 - nuRF)*2*pi;omega1 = nu1*2*pi;Omega = sqrt(omega1.^2 + omega.^2);Rxx = 1/2*(cos(Omega.*t).*Omega.^2+omega1.^2+cos(Omega.*t).*omega.^2)./(Omega.^2)+1/2*omega1.^2.*(1-cos(Omega.*t)).*cos(2*phi)./(Omega.^2);Ryy = 1/2*(cos(Omega.*t).*Omega.^2+omega1.^2+cos(Omega.*t).*omega.^2)./(Omega.^2)-1/2.*omega1.^2.*(1-cos(Omega.*t)).*cos(2*phi)./(Omega.^2);Rzz = omega.^2./(Omega.^2)+omega1.^2.*cos(Omega.*t)./(Omega.^2);Rxy = 1/2*omega1.^2.*(1-cos(Omega.*t)).*sin(2*phi)./(Omega.^2)-omega.*sin(Omega.*t)./Omega;Ryx = 1/2*omega1.^2.*(1-cos(Omega.*t)).*sin(2*phi)./(Omega.^2)+omega.*sin(Omega.*t)./Omega;Rxz = omega1.*omega.*(1-cos(Omega.*t)).*cos(phi)./(Omega.^2)+omega1.*sin(Omega.*t).*sin(phi)./Omega;Rzx = omega1.*omega.*(1-cos(Omega.*t)).*cos(phi)./(Omega.^2)-omega1.*sin(Omega.*t).*sin(phi)./Omega;Ryz = omega1.*omega.*(1-cos(Omega.*t)).*sin(phi)./(Omega.^2)-omega1.*sin(Omega.*t).*cos(phi)./Omega;Rzy = omega1.*omega.*(1-cos(Omega.*t)).*sin(phi)./(Omega.^2)+omega1.*sin(Omega.*t).*cos(phi)./Omega;